import React from 'react';
import '../../assets/stylesheets/ng-app/shared/footer.scss';

function About() {
    return (
        <div className='row'>
            <div className='col-md-12'>
                <div class="header-page"><div class="list-breadcrumbs left-right-offset"><div class="list-breadcrumbs-item"><div class="last-flex-item-container"><a href="/" class="uppercase">Home</a></div></div><div class="list-breadcrumbs-item"><div class="last-flex-item-container">About</div></div></div><div class="categories">Meek.ly</div><h1 class="title">About</h1></div>
            </div>
            <div className='col-md-12'>
                <ul class="list-menu-static-page"><li class="list-menu-static-page-item  active"><a href="/about">About us</a></li><li class="list-menu-static-page-item "><a href="/terms"> Terms of Use</a></li><li class="list-menu-static-page-item "><a href="/privacy"> Privacy Policy</a></li><li class="list-menu-static-page-item "><a href="/contactus"> Contact Us</a></li><li class="list-menu-static-page-item "><a href="/cookies"> What are Cookies</a></li></ul>
            </div>
            <div className='col-md-12'>
                <div class="left-right-offset"><div><br /></div><h2>All about Meek</h2><div><br /></div><p>Meek is simply a platform used to make the best Christian podcasts and radio stations in one place.&nbsp; We don't aggregate all that exist, but only the best.&nbsp; If you would like to have your radio station or podcast included, please <a href="https://www.meek.ly/contactus">fill out our contact form</a>.</p></div>
            </div>
        </div>
    )
}
export default About;
