import React from 'react';
import '../../assets/stylesheets/ng-app/layout/main.scss';
import '../../assets/stylesheets/ng-app/static_page.scss';
import AudioPlayer from 'react-modern-audio-player';

const playList = [
    {
        name: 'name',
        writer: 'writer',
        img: 'image.jpg',
        src: 'audio.mp4',
        id: 1,
    },
]

function MordenPlayer() {
    
    return (
        <div className='fixed-bottom'>
            <AudioPlayer
                playList={playList}
                audioInitialState={{
                    muted: false,
                    volume: 0.2,
                    curPlayId: 1,
                }}
                placement={{
                    interface: {
                        templateArea: {
                            trackTimeDuration: "row1-5",
                            progress: "row1-4",
                            playButton: "row1-6",
                            repeatType: "row1-7",
                            volume: "row1-8",
                        },
                    },
                    player: "bottom-left",
                }}
                activeUI={{
                    all: true,
                    progress: "bar",
                }}
            />
        </div>
    )
}
export default MordenPlayer;
