import React from 'react';
import '../../assets/stylesheets/ng-app/player/player.scss';
import AudioPlayer from 'react-modern-audio-player';

const playList = [
    {
        name: 'Despising and Rejecting Jesus Today',
        writer: 'Abdul Hameed',
        img: 'Greg_Laurie_Blog.png',
        src: 'audio.mp4',
        id: 1,
    },
]

function MordenPlayer() {

    return (
        <div className='fixed-bottom'>
            <AudioPlayer
                playList={playList}
                audioInitialState={{
                    muted: false,
                    volume: 0.7,
                    curPlayId: 1,
                    isPlaying: true,
                    currentTime: 0,
                    duration: 0,
                    // eslint-disable-next-line no-dupe-keys
                    curPlayId: 0
                }}
                placement={{
                    interface: {
                        templateArea: {
                            trackTimeDuration: "row1-5",
                            progress: "row1-4",
                            playButton: "row1-6",
                            repeatType: "row1-7",
                            volume: "row1-8",
                        },
                    },
                    player: "bottom-left",
                }}
                rootContainerProps={{
                    colorScheme: "dark"
                }}
                activeUI={{
                    all: true,
                    progress: "bar",
                }}
                autoplay
            />
        </div>
    )
}
export default MordenPlayer;
